import React from 'react';
import styles from './ScanSummary.module.css'

function ScanSummary() {
  const dummyData = {
    url: 'https://example.com',
    cookies: 8,
    trackers: 5,
    thirdParties: ['Google Analytics', 'Facebook Pixel', 'Hotjar'],
    privacyScore: 'C+',
  };

  const handleBlockCookies = () => {
    alert('Cookies would be blocked (simulation)');
    // Here, you can later implement real blocking logic
  };

  return (
    <div className={styles.container}>
      <div className={styles.card}>
      <h1>Scan Summary</h1>
      <p><strong>Website:</strong> {dummyData.url}</p>
      <p><strong>Cookies Detected:</strong> {dummyData.cookies}</p>
      <p><strong>Trackers Found:</strong> {dummyData.trackers}</p>
      <p><strong>Third-Party Services:</strong> {dummyData.thirdParties.join(', ')}</p>
      <p><strong>Privacy Score:</strong> {dummyData.privacyScore}</p>
      
      <button onClick={handleBlockCookies}>Block Cookies</button>
    </div>
    </div>
  );
}

export default ScanSummary;
