import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import styles from './ScanSummary.module.css';

function ScanSummary() {
  const { scanId } = useParams();
  const [scanData, setScanData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchScanSummary = async () => {
      try {
        const response = await axios.get(`http://localhost:3001/api/scan/summary/${scanId}`);

        setScanData(res.data);
      } catch (err) {
        console.error('Error fetching scan summary:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchScanSummary();
  }, [scanId]);

  const handleBlockCookies = () => {
    alert('Cookies would be blocked (simulation)');
  };

  return (
    <div className={styles.container}>
      <div className={styles.card}>
        <h1>Scan Summary</h1>
        {loading ? (
          <p>Loading scan details...</p>
        ) : scanData ? (
          <>
            <p><strong>Website:</strong> {scanData.url}</p>
            <p><strong>Date of scan:</strong> {scanData.date}</p>
            <p><strong>Total Cookies Detected:</strong> {scanData.cookies}</p>
            <p><strong>Total Trackers Found:</strong> {scanData.trackers}</p>
            <p><strong>Trackers origin:</strong> {scanData.origin}</p>
            <p><strong>IP Address:</strong> {scanData.ip}</p>
            <p><strong>Density:</strong> {scanData.density}</p>
            <p><strong>Risk rate:</strong> {scanData.riskrate}</p>
            <button className={styles.pbtton} onClick={handleBlockCookies}>Block Cookies</button>
          </>
        ) : (
          <p>Could not load scan data.</p>
        )}
      </div>

      <div className={styles.footer}>
        <p className={styles.footp}>Built with passion, precision, and a commitment to your security. Made with ❤️ by CODE CREATURES.</p>
      </div>
    </div>
  );
}

export default ScanSummary;
